package com.vibot.voice.tts

import org.hamcrest.CoreMatchers.`is`
import org.hamcrest.MatcherAssert.assertThat
import org.junit.After
import org.junit.Test
import java.io.File

private const val AUDIO_ID = "temporal_id"

class AudioBuilderIT {

    private val builder = AudioBuilder(CommandRunner())

    @After
    fun tearDown() {
        audioFile().delete()
    }

    @Test
    fun `given short text should build audio wav`() {
        builder.build(AUDIO_ID, "hello world")

        assertThat(audioFile().exists(), `is`(true))
    }

    @Test
    fun `given long text should build audio wav`() {
        builder.build(AUDIO_ID, text())

        assertThat(audioFile().exists(), `is`(true))
    }

    private fun audioFile() = File("audios/$AUDIO_ID.wav")

